query:
query allMatch($after: String, $before: String, $first: Int, $last: Int, $liveOnly: Boolean, $sports: [ID!], $tournaments: [ID!], $historic: Boolean, $favourites: Boolean, $search: String) {
  allMatch(
    after: $after
    before: $before
    first: $first
    last: $last
    liveOnly: $liveOnly
    sports: $sports
    tournaments: $tournaments
    historic: $historic
    favourites: $favourites
    text: $search
  ) {
    edges {
      node {
        id
        marketsCount
        stream {
          url
          streamProvider
          __typename
        }
        favourite
        ...MatchBase
        ...MatchMainMarketGroups
        ...MatchPeriods
        ...MatchScoreboard
        __typename
      }
      __typename
    }
    pageInfo {
      hasNextPage
      hasPreviousPage
      startCursor
      endCursor
      __typename
    }
    total
    __typename
  }
}

fragment MatchBase on Match {
  datePlannedStart
  state
  prematchOnly
  ...MatchTeams
  ...MatchTournament
  __typename
}

fragment MatchTeams on Match {
  teams {
    team {
      id
      name
      icon
      __typename
    }
    winner
    __typename
  }
  __typename
}

fragment MatchTournament on Match {
  tournament {
    id
    name
    favourite
    sport {
      id
      icon
      name
      __typename
    }
    __typename
  }
  __typename
}

fragment MatchMainMarketGroups on Match {
  mainMarketGroups {
    id
    name
    namePrefix
    layoutType
    independenceId
    selections {
      id
      name
      __typename
    }
    markets {
      id
      info
      state
      outcomes {
        id
        odds
        state
        won
        __typename
      }
      __typename
    }
    __typename
  }
  __typename
}

fragment MatchPeriods on Match {
  periods {
    number
    homeScore
    awayScore
    periodScore {
      ... on KillsPeriodScore {
        homeKills
        awayKills
        __typename
      }
      ... on RoundsPeriodScore {
        homeWonRounds
        awayWonRounds
        __typename
      }
      ... on GoalsPeriodScore {
        homeGoals
        awayGoals
        __typename
      }
      __typename
    }
    __typename
  }
  activePeriod
  __typename
}

fragment MatchScoreboard on Match {
  scoreboard {
    ... on CsgoScoreboard {
      homeCtTeam
      homeWonRounds
      awayWonRounds
      currentRound
      __typename
    }
    ... on CsgoWingmanScoreboard {
      homeCtTeam
      homeWonRounds
      awayWonRounds
      currentRound
      __typename
    }
    ... on LolScoreboard {
      homeKills
      awayKills
      homeDestroyedTurrets
      awayDestroyedTurrets
      homeGold
      awayGold
      __typename
    }
    ... on Dota2Scoreboard {
      homeKills
      awayKills
      homeDestroyedTowers
      awayDestroyedTowers
      homeGold
      awayGold
      __typename
    }
    ... on KogScoreboard {
      homeKills
      awayKills
      homeDestroyedTurrets
      awayDestroyedTurrets
      homeGold
      awayGold
      __typename
    }
    ... on RSoccerScoreboard {
      homeGoals
      awayGoals
      __typename
    }
    ... on ValorantScoreboard {
      homeDefenderTeam
      homeWonRounds
      awayWonRounds
      currentRound
      __typename
    }
    __typename
  }
  __typename
}
